SELECT TITLE, u.BOARD_ID, s.REPLY_ID, s.WRITER_ID, s.CONTENTS, DATE_FORMAT(s.CREATED_DATE,'%Y-%m-%d') as CREATED_DATE from USED_GOODS_BOARD as u right join USED_GOODS_REPLY as s on u.BOARD_ID = s.BOARD_ID where DATE_FORMAT(u.CREATED_DATE,'%Y-%m') = '2022-10' order by s.CREATED_DATE asc, u.TITLE asc;